css:
/* General Styles */
.invoice-container {
    font-family: Arial, sans-serif;
    font-size: 12px;
    color: #333;
    width: 100%;
    margin: 0 auto;
    padding: 20px;
    page-break-inside: avoid;
}

/* Header Section */
.header {
    text-align: center;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 2px solid #ddd;
}

.logo {
    display: block;
    margin: 0 auto;
    width: 350px;
    height: auto;
}

/* Hospital Info Section */
.hospital-info {
    text-align: center;
    font-size: 11px;
    color: #555;
    margin-top: 10px;
}

/* Styled Company Name */
.hospital-info .company {
    font-size: 18px; /* Larger font size for emphasis */
    font-weight: bold; /* Bold font to make it stand out */
    color: #0056b3; /* Blue color for branding */
    text-transform: uppercase; /* Makes the text uppercase for authority */
    margin-bottom: 10px; /* Adds spacing below the name */
}

/* Invoice Number Styling */
.invoice-number {
    font-size: 16px;
    font-weight: bold;
    color: #0056b3;
    text-align: center;
    margin-top: 15px;
    margin-bottom: 20px;
}

/* Details Section */
.details {
    margin-bottom: 20px;
}

.details table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 10px;
}

.details td {
    padding: 8px 5px;
    vertical-align: top;
}

/* Invoice Table */
.invoice-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 20px;
}

.invoice-table th {
    background-color: #f9f9f9;
    border: 1px solid #ddd;
    padding: 10px;
    text-align: left;
    font-weight: bold;
}

.invoice-table td {
    border: 1px solid #ddd;
    padding: 8px;
}

/* Totals Section */
.totals-container {
    text-align: right;
    font-weight: bold;
    margin-top: 20px;
    padding-top: 10px;
    border-top: 1px solid #ddd;
}

.totals-container .amount-due {
    color: #e53935;
    font-size: 14px;
    font-weight: bold;
}

/* Category Header */
h4 {
    font-size: 14px;
    margin-top: 20px;
    color: #0056b3;
}

/* Page Break */
.page-break {
    page-break-before: always;
}
